package practiceJavaDemo;

public class Break_Continue {
//Example of the Break and Continue statement
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 //For loop to return value from 1 to 10
		for(int i=1; i<=10; i++)
        {
       	 if(i == 1)
       	 {
       		 continue;   // break;
       	 }
       	 System.out.println(i);
        }
	

	}

}
